import './field.css'
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';

interface FieldProps {
  labelValue: string;
  inputType: string;
}

const Field = ({ labelValue, inputType }: FieldProps) => {
  const lowerCaseLabel: string = labelValue.toLowerCase();
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="field-container">
      <label>{labelValue + ": "}</label>
      <div className='password-container'>
        <input type={inputType} name={lowerCaseLabel} 
        placeholder={"Enter " + lowerCaseLabel} required/>
        { lowerCaseLabel === 'password' && (
          <button className='eye-button' onClick={() => setShowPassword(!showPassword)}>
            {showPassword
            ? <FontAwesomeIcon icon={faEye}/>
            : <FontAwesomeIcon icon={faEyeSlash}/>}
          </button>
        )}
      </div>
    </div>
  );
}

export default Field;
