import './field.css'
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';

interface FieldProps {
  labelValue: string;
  inputType: string;
}

const Field = ({ labelValue, inputType }: FieldProps) => {
  const lowerCaseLabel: string = labelValue.toLowerCase();
  const [showPassword, setShowPassword] = useState(false);

  const isPasswordCheck = (): boolean => {
    return lowerCaseLabel === 'password';
  }

  const getType = () => {
    isPasswordCheck() && (
      showPassword ? 'text' : inputType
    );
  }

  return (
    <div className="field-container">
      <label>{labelValue + ": "}</label>
      <div className='password-container'>
        <input type={ getType } name={lowerCaseLabel} 
        placeholder={"Enter " + lowerCaseLabel} required/>
        { isPasswordCheck() && (
          <button className='eye-button' onClick={() => setShowPassword(!showPassword)}>
            <FontAwesomeIcon icon={showPassword ? faEye : faEyeSlash} />
          </button>
        )}
      </div>
    </div>
  );
}

export default Field;
